const menuBtn=document.querySelector("#menu-btn"),menu=document.querySelector("header .cont > div");menuBtn.onclick=()=>{menuBtn.classList.toggle("menu-btn"),menu.classList.toggle("menu")};for(var slider=document.querySelector(".pics img"),indicators=document.querySelector(".btns"),images=document.querySelectorAll(".btns img"),i=0;i<images.length;i++)images[i].setAttribute("data-index",i),images[i].addEventListener("click",e=>{change(e.currentTarget.getAttribute("data-index"))});function change(e){for(var t=0;t<images.length;t++)images[t].classList.remove("active"),images[t].getAttribute("data-index")==e&&(images[t].classList.add("active"),slider.setAttribute("src",images[t].getAttribute("src")))}